lick(function(){	var Sector = $(this).attr('id');	$('#Sector').val(Sector);	$('.selected .fa').removeClass('fa-check');$('.selected .fa').addClass('fa-circle');$('.radio_circle').removeClass('selected');$(this).addClass('selected');$('.selected .fa').removeClass('fa-circle');$('.selected .fa').addClass('fa-check');});$('.radio_circle-group2 .radio_circle2').click(function(){	var License_type = $(this).attr('id');	$('#Company_License_type').val(License_type);	$('.selected .fa').removeClass('fa-check');$('.selected .fa').addClass('fa-circle');$('.radio_circle2').removeClass('selected');$(this).addClass('selected');$('.selected .fa').removeClass('fa-circle');$('.selected .fa').addClass('fa-check');});$('.radio_circle-group3 .radio_circle3').click(function(){	var loyalty_rule_setup = $(this).attr('id');	$('#loyalty_rule_setup').val(loyalty_rule_setup);	$('.selected .fa').removeClass('fa-check');$('.selected .fa').addClass('fa-circle');$('.radio_circle3').removeClass('selected');$(this).addClass('selected');$('.selected .fa').removeClass('fa-circle');$('.selected .fa').addClass('fa-check');});$('.radio_circle-group4 .radio_circle4').click(function(){$('#bonus_circle').hide();$('#bonus_check').show();$('#Joining_bonus_block').show();$('#Joining_bonus').val(1);/* $('.selected .fa').removeClass('fa-check');$('.selected .fa').addClass('fa-circle');$('.radio_circle4').removeClass('selected');$(this).addClass('selected');$('.selected .fa').removeClass('fa-circle');$('.selected .fa').addClass('fa-check'); */});$('.radio_circle-group44 .radio_circle44').click(function(){	var Joining_bonus_points = $('#Joining_bonus_points').val();	$('#bonus_circle').show();$('#bonus_check').hide();$('#Joining_bonus_block').hide();$('#Joining_bonus_points').val(0);$('#Joining_bonus').val(0);	// alert('Joining_bonus_points '+Joining_bonus_points);Joining_bonus_block/* $('.selected .fa').removeClass('fa-check');$('.selected .fa').addClass('f